import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";
import React, { useState } from "react";

function AccountContainer() {
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [allTransactions] = useState();

  const handleSearch = () => {
    const filt = allTransactions.filter((transaction) =>
      transaction)

    setFilteredTransactions(filt);
  };

  return (
    <div>
      <Search transactions={allTransactions} onSearch={handleSearch} />
      <AddTransactionForm />
      <TransactionsList transactions={filteredTransactions.length > 0 ? filteredTransactions : allTransactions} />
    </div>
  );
}

export default AccountContainer;
