import AccountContainer from "./AccountContainer";
import React, { useState } from "react";
import Search from "./Search";

function App() {
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [allTransactions] = useState(/* Your initial data */);

  const handleSearch = (query) => {
    const filtered = allTransactions.filter((transaction) =>
      transaction.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredTransactions(filtered);
  };

  return (
    <div className="ui raised segment">
      <div className="ui segment violet inverted">
        <h2>The Royal Bank of Flatiron</h2>
      </div>
      <Search transactions={allTransactions} onSearch={handleSearch} />
      <AccountContainer transactions={filteredTransactions} />
    </div>
  );
}

export default App;
