import React, { useState } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer() {
  const [searchTerm, setSearchTerm] = useState(""); // State for search term
  // eslint-disable-next-line
  const [filteredTransactions, setFilteredTransactions] = useState([]); // State for filtered transactions

  // Function to update filtered transactions based on search term
  const handleSearch = (searchTerm) => {
    setSearchTerm(searchTerm);
  };

  return (
    <div>
      {/* Pass handleSearch function and searchTerm to the Search component */}
      <Search onSearch={handleSearch} />
      {/* Pass searchTerm to the TransactionsList component */}
      <TransactionsList searchTerm={searchTerm} />
      <AddTransactionForm />
    </div>
  );
}

export default AccountContainer;
