import React, { useState ,useEffect} from "react";

function Search() {
  const [transactions, setTransactions] = useState([]); // Initialize state for transactions
  const [searchTerm, setSearchTerm] = useState(""); // Initialize state for search term

  // Fetch transactions data (you can use useEffect to fetch data from an API)
  // For simplicity, I'm using your provided data directly

    useEffect(() => {
      fetch("http://localhost:8001/transactions")
        .then((response) => response.json())
        .then((data) => {
          setTransactions(data);
        });
    }, []);
    // Assuming you fetch the transactions data from an API endpoint
    // For now, I'm setting it directly from the provided JSON


  // Filtering function to search transactions based on the description
  const filteredTransactions = transactions.filter(transaction =>
    transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <div className="ui large fluid icon input">
        <input
          type="text"
          placeholder="Search your Recent Transactions"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <i className="circular search link icon"></i>
      </div>
      {/* Render filtered transactions */}
      <ul>
        {filteredTransactions.map(transaction => (
          <li key={transaction.id}>
            {transaction.description} - ${transaction.amount}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Search;
